mapper.select:
select t.id, t.timestamp, t.EntityFileName, t.strIMEI, t.strMAC, t.strImsi, strAppName, strDisplayname, strAppVer, szMainFileName, nOPType, t1.strMSISDN as msisdn, t2.summaryName from t_sjapp t LEFT JOIN t_sjinfo t1 ON t.strImsi = t1.strIMSI and t1.strMSISDN != "", t_summary t2 where t.PID = t2.id
AND t.id < #{id, jdbcType=INTEGER}
AND t.timestamp > #{starttime, jdbcType=INTEGER}
AND t.timestamp < #{endTime, jdbcType=INTEGER}
AND (t.strIMEI like '%${likeQueryKey}%' OR t.strImsi like '%${likeQueryKey}%' OR t.strAppName like '%${likeQueryKey}%')
AND ${summaryIds}
group by t.id order by t.id desc limit 101

mapper.delete:

delete from t_sjapp
where timestamp > #{starttime, jdbcType=INTEGER} 
AND timestamp < #{endTime, jdbcType=INTEGER}
