mapper.select:
select 

from t_sjinfo t
where id > #{id,jdbcType=INTEGER}
and timestamp > #{starttime, jdbcType=INTEGER}


	AND timestamp < #{endTime, jdbcType=INTEGER}




	AND (t.strIMEI like '%${likeQueryKey}%' 
		OR t.strIMSI like '%${likeQueryKey}%'
		OR t.strMSISDN like '%${likeQueryKey}%')




	AND ${summaryIds}


order by id desc
limit 101

mapper.delete:

delete from t_sjinfo
where timestamp > #{starttime, jdbcType=INTEGER} 
AND timestamp < #{endTime, jdbcType=INTEGER}
