mapper.select:
select t.id, t.timestamp, strPhoneNum, strFromPhoneNum, strToPhoneNum, strContent, nFragCount, nFragIndex, t1.summaryName, t2.strIMSI from t_sms t LEFT JOIN t_sjinfo t2 ON t2.strMSISDN = t.strPhoneNum, t_summary t1 where t.PID = t1.id
AND t.id < #{id,jdbcType=INTEGER}
AND t.timestamp > #{starttime, jdbcType=INTEGER}
AND t.timestamp < #{endTime, jdbcType=INTEGER}
AND (t.strphonenum like '%${likeQueryKey}%' OR t.strfromphonenum like '%${likeQueryKey}%' OR t.strtophonenum like '%${likeQueryKey}%' OR t.strcontent like '%${likeQueryKey}%')
AND ${summaryIds}
group by t.id order by t.id desc limit 101

mapper.delete:

delete from t_sms
where timestamp > #{starttime, jdbcType=INTEGER} 
AND timestamp < #{endTime, jdbcType=INTEGER}
