mapper.select:
select t.id, t.timestamp, t.EntityFileName, strRelateID, strPhoneNum, strFromUsername, strFromPhoneNum, strToUsername, strToPhoneNum, nDataType, Status, OpType, CodecType, ToolType, szPCapStartTime, szPCapEndTime, t1.summaryName, t2.strIMSI from t_voip t LEFT JOIN t_sjinfo t2 ON t2.strMSISDN = t.strPhoneNum, t_summary t1 where t.PID = t1.id
AND t.id < #{id, jdbcType=INTEGER}
and t.timestamp > #{starttime, jdbcType=INTEGER}
AND t.timestamp < #{endTime, jdbcType=INTEGER}
AND (t.strphonenum like '%${likeQueryKey}%' OR t.strfromphonenum like '%${likeQueryKey}%' OR t.strtophonenum like '%${likeQueryKey}%')
AND ${summaryIds}
group by t.id order by t.id desc limit 101


select 
EntityFileName
from t_voip t
where timestamp > #{starttime, jdbcType=INTEGER}
	AND timestamp < #{endTime, jdbcType=INTEGER}

mapper.delete:

delete from t_voip
where timestamp > #{starttime, jdbcType=INTEGER} 
AND timestamp < #{endTime, jdbcType=INTEGER}
